  isHidden: boolean;
  category?: 'preferred' | 'sponsored' | 'general'; // Preferred Roofer, Sponsored Result, General Listing
  sortOverride?: number;
  serviceAreas: {
    regions?: string[]; // region slugs
    counties?: string[]; // county slugs
    cities?: string[]; // city slugs
  };
  // Additional fields that might be in your Excel
