// Helper to get all roofers
export function getAllRoofers(): RooferData[] {
  return Object.values(rooferData).filter(roofer => !roofer.isHidden);
}

// Helper to get preferred/certified roofers
export function getPreferredRoofers(): RooferData[] {
  return getAllRoofers()
    .filter(roofer => roofer.isPreferred || roofer.category === 'preferred')
    .sort((a, b) => {
      // Sort by sortOverride if available, then by name
      if (a.sortOverride !== undefined && b.sortOverride !== undefined) {
        return a.sortOverride - b.sortOverride;
      }
      if (a.sortOverride !== undefined) return -1;
      if (b.sortOverride !== undefined) return 1;
      return a.name.localeCompare(b.name);
    });
}

// Helper to get sponsored roofers
export function getSponsoredRoofers(): RooferData[] {
  return getAllRoofers()
    .filter(roofer => roofer.category === 'sponsored')
    .sort((a, b) => {
      if (a.sortOverride !== undefined && b.sortOverride !== undefined) {
        return a.sortOverride - b.sortOverride;
      }
      if (a.sortOverride !== undefined) return -1;
      if (b.sortOverride !== undefined) return 1;
      return a.name.localeCompare(b.name);
    });
}

// Helper to get general listings
export function getGeneralRoofers(): RooferData[] {
  return getAllRoofers()
    .filter(roofer => roofer.category === 'general')
    .sort((a, b) => {
      if (a.sortOverride !== undefined && b.sortOverride !== undefined) {
        return a.sortOverride - b.sortOverride;
      }
      if (a.sortOverride !== undefined) return -1;
      if (b.sortOverride !== undefined) return 1;
      return a.name.localeCompare(b.name);
    });
}

// Helper to get other (non-preferred, non-certified) roofers
export function getOtherRoofers(): RooferData[] {
  return getAllRoofers()
    .filter(roofer => !roofer.isPreferred && roofer.category !== 'preferred')
    .sort((a, b) => {
      // Category ranking: Sponsored before General
      const categoryRank = (category?: string) => {
        if (category === 'sponsored') return 1;
        if (category === 'general') return 2;
        return 3;
      };
      
      const rankA = categoryRank(a.category);
      const rankB = categoryRank(b.category);
      
      if (rankA !== rankB) return rankA - rankB;
      
      if (a.sortOverride !== undefined && b.sortOverride !== undefined) {
        return a.sortOverride - b.sortOverride;
      }
      if (a.sortOverride !== undefined) return -1;
      if (b.sortOverride !== undefined) return 1;
      return a.name.localeCompare(b.name);
    });
}

// Helper to get roofer by slug
export function getRooferBySlug(slug: string): RooferData | null {
  // First try direct lookup by Record key (for backwards compatibility)
  if (rooferData[slug] && !rooferData[slug].isHidden) {
    return rooferData[slug];
  }
  
  // If not found, search by slug property (in case Record keys don't match slugs)
  const roofer = Object.values(rooferData).find(
    (r) => r.slug === slug && !r.isHidden
  );
  
  return roofer || null;
}

// Helper to get roofers by service area
export function getRoofersByServiceArea(
  regionSlug?: string,
  countySlug?: string,
  citySlug?: string
): RooferData[] {
  const allRoofers = getAllRoofers();
  
  return allRoofers.filter(roofer => {
    const { serviceAreas } = roofer;
    
    // If city is specified, check city first
    if (citySlug) {
      return serviceAreas.cities?.includes(citySlug) ||
             serviceAreas.counties?.some(county => {
               // If roofer serves the county, they serve all cities in it
               return county === countySlug;
             }) ||
             serviceAreas.regions?.some(region => {
               // If roofer serves the region, they serve all areas in it
               return region === regionSlug;
             });
    }
    
    // If county is specified
    if (countySlug) {
      return serviceAreas.counties?.includes(countySlug) ||
             serviceAreas.regions?.some(region => region === regionSlug);
    }
    
    // If region is specified
    if (regionSlug) {
      return serviceAreas.regions?.includes(regionSlug);
    }
    
    return false;
  }).sort((a, b) => {
    // Category ranking: Certified (preferred) first, then Sponsored, then General
    const categoryRank = (category?: string, isPreferred?: boolean) => {
      if (category === 'preferred' || isPreferred) return 1; // Certified/Preferred
      if (category === 'sponsored') return 2; // Sponsored
      if (category === 'general') return 3; // General
      return 4; // No category
    };
    
    const rankA = categoryRank(a.category, a.isPreferred);
    const rankB = categoryRank(b.category, b.isPreferred);
    
    if (rankA !== rankB) return rankA - rankB;
    
    // Then by sortOverride
    if (a.sortOverride !== undefined && b.sortOverride !== undefined) {
      return a.sortOverride - b.sortOverride;
    }
    if (a.sortOverride !== undefined) return -1;
    if (b.sortOverride !== undefined) return 1;
    
    // Finally by name
    return a.name.localeCompare(b.name);
  });
}








